export default {
  name: "TenderDetail",
  data: function data() {
    return {
      tenderAmount: "",
      certificateAmount: "",
      dialogs: {
        warning: false,
        apply: false,
        create: false
      }
    };
  },
  methods: {
    sureClick: function sureClick(tenderAmount, certificateAmount) {
      if (tenderAmount > certificateAmount) {
        this.dialogs.warning = true;
        console.log("this.dialogs.warning: " + this.dialogs.warning);
      } else if (tenderAmount == certificateAmount) {
        this.dialogs.apply = true;
        console.log("this.dialogs.apply: " + this.dialogs.apply);
      }
    },
    createClick: function createClick() {
      this.dialogs.create = true;
      console.log("this.dialogs.create: " + this.dialogs.create);
    }
  },
  mounted: function mounted() {
    this.tenderAmount = this.$route.query.amount;
    this.certificateAmount = this.$store.state.currentCertificateAmount;
  },
  components: {
    "dialog-warning": require("@/components/TenderDetail/Dialog/DialogWarning.vue")["default"],
    "dialog-sure-apply": require("@/components/TenderDetail/Dialog/DialogSureApply.vue")["default"],
    "dialog-create-certificate": require("@/components/TenderDetail/Dialog/DialogCreateCertificate.vue")["default"],
    "list-certificate": require("@/components/TenderDetail/ListCertificate.vue")["default"]
  }
};
