import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    tenders: [
      {
        id: "NCHC-P-106104",
        title: "人工智慧超級電腦採購案",
        amount: "49,000,000",
        relaseDate: "2017-10-25",
        terminationDate: "2017-12-01",
      },
      {
        id: "NCHC-S-109003",
        title: "109年雲端服務及大數據運算平台(台灣杉2)維護案",
        amount: "70,000,000",
        relaseDate: "2020-01-03",
        terminationDate: "2020-02-07",
      },
      {
        id: "NCHC-P-107241",
        title: "工程科學多尺度雲端服務平台租用採購案",
        amount: "9,000,000",
        relaseDate: "2018-11-20",
        terminationDate: "2018-12-04",
      },
      {
        id: "NCHC-P-109001",
        title: "先進人工智慧大數據計算主機與儲存系統",
        amount: "860,000,000",
        relaseDate: "2020-05-08",
        terminationDate: "2020-07-03",
      },
    ],
    tenderDetails: [
      {
        name: "投標案資訊 :",
        children: [
          { name: `標案名稱 : {{ title }}`},
          { name: "標案案號 : app" },
          { name: "標案金額 : app" },
        ],
      },

    ]
  },
  getters: {
  },
  mutations: {
    listDetail(state) {
      let newTask = {
        name: "投標案資訊 :",
        children: [
          { name: `標案名稱 : state.tenders.title`},
          { name: "標案案號 : state.tenders.id" },
          { name: "標案金額 : state.tenders.amount" },
        ],
      }
      state.tenderDetails.push(newTask)
    }

  },
  actions: {
  },
  modules: {
  }
})
